package org.emptybit.luppy;

import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;

import java.util.ArrayList;

public class ShopActivity extends AppCompatActivity {

    RecyclerView xListView;
    ArrayList<ProductModel> productList;
    ShopAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_shop);

        xListView = findViewById(R.id.shop_items_to_display);
        xListView.setLayoutManager(new LinearLayoutManager(this));

        productList = new ArrayList<>();

        productList.add(new ProductModel("data:image/jpeg;base64,", 2000));
        productList.add(new ProductModel("data:image/jpeg;base64,", 1500));
        productList.add(new ProductModel("data:image/jpeg;base64,", 1480));
        productList.add(new ProductModel("data:image/jpeg;base64,", 1600));
        productList.add(new ProductModel("data:image/jpeg;base64,", 2000));
        productList.add(new ProductModel("data:image/jpeg;base64,", 2400));

        adapter = new ShopAdapter(getApplicationContext(), productList);
        xListView.setAdapter(adapter);

    }
}
